using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataAchievement{		/**		* id		*/		public int Id;		/**		* 分组		*/		public int Team;		/**		* 转生等级		*/		public int ReLevelLimit;		/**		* 等级		*/		public int LevelLimit;		/**		* 前置ID		*/		public int LastID;		/**		* 后续ID		*/		public int NextID;		/**		* 计数模式		*/		public int CountMode;		/**		* 条件类型		*/		public int[] ConditionType;		/**		* 计数		*/		public int[] CheckCount;		/**		* 比对ID		*/		public int[] CheckID;		/**		* 奖励模式		*/		public int AwardMode;		/**		* 称号奖励		*/		public int AwardTitle;		/**		* 奖励成就点		*/		public int AwardPoint;		/**		* 奖励绑金		*/		public int AwardGoldBind;		/**		* 奖励绑元		*/		public int AwardVipPointBind;		/**		* 物品ID(通用)		*/		public int[] ItemBaseID;		/**		* 物品数量(通用)		*/		public int[] ItemCount;		/**		* 物品ID(男战士)		*/		public int[] ZhanMaleID;		/**		* 物品ID(女战士)		*/		public int[] ZhanFemaleID;		/**		* 物品ID(男法师)		*/		public int[] FaMaleID;		/**		* 物品ID(女法师)		*/		public int[] FaFemaleID;		/**		* 物品ID(男道士)		*/		public int[] DaoMaleID;		/**		* 物品ID(女道士)		*/		public int[] DaoFemaleID;		/**		* 名称		*/		public string Name;		/**		* 描述		*/		public string Desc;		/**		* 图标		*/		public string Icon;		/**		* 是否显示此成就		*/		public int Show;		/**		* 是否显示进度		*/		public int ShowProgress;		static public Dictionary<int, TbDataAchievement> temples=new Dictionary<int,TbDataAchievement>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataAchievement tp=new TbDataAchievement();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Team=(int)tb["Team"];				tp.ReLevelLimit=(int)tb["ReLevelLimit"];				tp.LevelLimit=(int)tb["LevelLimit"];				tp.LastID=(int)tb["LastID"];				tp.NextID=(int)tb["NextID"];				tp.CountMode=(int)tb["CountMode"];				tp.ConditionType=(int[])tb["ConditionType"];				tp.CheckCount=(int[])tb["CheckCount"];				tp.CheckID=(int[])tb["CheckID"];				tp.AwardMode=(int)tb["AwardMode"];				tp.AwardTitle=(int)tb["AwardTitle"];				tp.AwardPoint=(int)tb["AwardPoint"];				tp.AwardGoldBind=(int)tb["AwardGoldBind"];				tp.AwardVipPointBind=(int)tb["AwardVipPointBind"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.ZhanMaleID=(int[])tb["ZhanMaleID"];				tp.ZhanFemaleID=(int[])tb["ZhanFemaleID"];				tp.FaMaleID=(int[])tb["FaMaleID"];				tp.FaFemaleID=(int[])tb["FaFemaleID"];				tp.DaoMaleID=(int[])tb["DaoMaleID"];				tp.DaoFemaleID=(int[])tb["DaoFemaleID"];				tp.Name=(string)tb["Name"];				tp.Desc=(string)tb["Desc"];				tp.Icon=(string)tb["Icon"];				tp.Show=(int)tb["Show"];				tp.ShowProgress=(int)tb["ShowProgress"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataAchievement select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}