using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataAchievementPointAward{		/**		* ID		*/		public int Id;		/**		* 所需成就点数		*/		public int AchievementPoint;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataAchievementPointAward> temples=new Dictionary<int,TbDataAchievementPointAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataAchievementPointAward tp=new TbDataAchievementPointAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.AchievementPoint=(int)tb["AchievementPoint"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataAchievementPointAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}