using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataActiveValueAward{		/**		* ID		*/		public int Id;		/**		* 所需签到次数		*/		public int Value;		/**		* 物品ID		*/		public int[] ItemBaseID;		/**		* 物品数量		*/		public int[] ItemCount;		/**		* 描述		*/		public string Desc;		static public Dictionary<int, TbDataActiveValueAward> temples=new Dictionary<int,TbDataActiveValueAward>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataActiveValueAward tp=new TbDataActiveValueAward();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Value=(int)tb["Value"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.Desc=(string)tb["Desc"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataActiveValueAward select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}