using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataActiveValueGet{		/**		* id		*/		public int Id;		/**		* 条件类型		*/		public int ConditionType;		/**		* 计数		*/		public int CheckCount;		/**		* 比对ID		*/		public int CheckID;		/**		* 名称		*/		public string Name;		/**		* 奖励模式		*/		public int AwardMode;		/**		* 奖励活跃值		*/		public int Award;		/**		* 活动时间		*/		public string Time;		/**		* 获得方式显示		*/		public string Show;		/**		* 客户端功能ID		*/		public int ClientFunctionId;		/**		* 客户端功能参数		*/		public string[] ClientFunctionParams;		/**		* 悬浮提示		*/		public string Tips;		static public Dictionary<int, TbDataActiveValueGet> temples=new Dictionary<int,TbDataActiveValueGet>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataActiveValueGet tp=new TbDataActiveValueGet();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.ConditionType=(int)tb["ConditionType"];				tp.CheckCount=(int)tb["CheckCount"];				tp.CheckID=(int)tb["CheckID"];				tp.Name=(string)tb["Name"];				tp.AwardMode=(int)tb["AwardMode"];				tp.Award=(int)tb["Award"];				tp.Time=(string)tb["Time"];				tp.Show=(string)tb["Show"];				tp.ClientFunctionId=(int)tb["ClientFunctionId"];				tp.ClientFunctionParams=(string[])tb["ClientFunctionParams"];				tp.Tips=(string)tb["Tips"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataActiveValueGet select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}