using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataVip{		/**		* ID		*/		public int Id;		/**		* vip等级		*/		public int Level;		/**		* 需要充值元宝数		*/		public int NeedMoney;		/**		* 备注说明		*/		public int[] ItemUseType;		/**		* 每日使用金币符个数		*/		public int[] ItemUseNum;		/**		* 封魔领最大倍数		*/		public int MaxFMLPrc;		/**		* 每日经验buff倍数		*/		public int ExpPrc;		/**		* 每日经验buff小时数		*/		public int ExpHour;		/**		* buffID		*/		public int BuffID;		/**		* 最小物攻		*/		public int MinATK;		/**		* 最大物攻		*/		public int MaxATK;		/**		* 最小法攻		*/		public int MinMATK;		/**		* 最大法攻		*/		public int MaxMATK;		/**		* 最小道攻		*/		public int MinSATK;		/**		* 最大道攻		*/		public int MaxSATK;		/**		* 原地复活次数		*/		public int ReliveTime;		/**		* 奖励物品		*/		public int[] ItemBaseID;		/**		* 奖励物品数		*/		public int[] ItemCount;		/**		* 挂机地图		*/		public int[] MapID;		/**		* 图标		*/		public string Icon1;		/**		* 图标		*/		public string Icon2;		/**		* 图标		*/		public string Icon3;		/**		* buff图标		*/		public string BuffIcon;		static public Dictionary<int, TbDataVip> temples=new Dictionary<int,TbDataVip>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataVip tp=new TbDataVip();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Level=(int)tb["Level"];				tp.NeedMoney=(int)tb["NeedMoney"];				tp.ItemUseType=(int[])tb["ItemUseType"];				tp.ItemUseNum=(int[])tb["ItemUseNum"];				tp.MaxFMLPrc=(int)tb["MaxFMLPrc"];				tp.ExpPrc=(int)tb["ExpPrc"];				tp.ExpHour=(int)tb["ExpHour"];				tp.BuffID=(int)tb["BuffID"];				tp.MinATK=(int)tb["MinATK"];				tp.MaxATK=(int)tb["MaxATK"];				tp.MinMATK=(int)tb["MinMATK"];				tp.MaxMATK=(int)tb["MaxMATK"];				tp.MinSATK=(int)tb["MinSATK"];				tp.MaxSATK=(int)tb["MaxSATK"];				tp.ReliveTime=(int)tb["ReliveTime"];				tp.ItemBaseID=(int[])tb["ItemBaseID"];				tp.ItemCount=(int[])tb["ItemCount"];				tp.MapID=(int[])tb["MapID"];				tp.Icon1=(string)tb["Icon1"];				tp.Icon2=(string)tb["Icon2"];				tp.Icon3=(string)tb["Icon3"];				tp.BuffIcon=(string)tb["BuffIcon"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataVip select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}