using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataWing{		/**		* ID		*/		public int Id;		/**		* 等级		*/		public int Level;		/**		* 升级所需羽毛		*/		public int NeedFeater;		/**		* 所需绑金		*/		public int NeedGold;		/**		* 升级概率		*/		public int UpStepPro;		/**		* 翅膀对应物品ID		*/		public int ItemID;		/**		* 下阶翅膀ID		*/		public int NextitemID;		/**		* 最小次数		*/		public int MinUpStepTimes;		/**		* 最大次数		*/		public int MaxUpStepTimes;		/**		* 最小祝福值		*/		public int MinRandBless;		/**		* 最大祝福值		*/		public int MaxRandBless;		/**		* 最大祝福		*/		public int MaxBless;		/**		* 描述		*/		public string Name;		static public Dictionary<int, TbDataWing> temples=new Dictionary<int,TbDataWing>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataWing tp=new TbDataWing();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.Level=(int)tb["Level"];				tp.NeedFeater=(int)tb["NeedFeater"];				tp.NeedGold=(int)tb["NeedGold"];				tp.UpStepPro=(int)tb["UpStepPro"];				tp.ItemID=(int)tb["ItemID"];				tp.NextitemID=(int)tb["NextitemID"];				tp.MinUpStepTimes=(int)tb["MinUpStepTimes"];				tp.MaxUpStepTimes=(int)tb["MaxUpStepTimes"];				tp.MinRandBless=(int)tb["MinRandBless"];				tp.MaxRandBless=(int)tb["MaxRandBless"];				tp.MaxBless=(int)tb["MaxBless"];				tp.Name=(string)tb["Name"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataWing select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}