using System.Collections.Generic;using System.Collections;using GameLib.game;using GameServer.Define.EnumNormal;namespace GameDb.Data{	public class TbDataWorldBoss{		/**		* ID		*/		public int Id;		/**		* 单位静态ID		*/		public int BossBaseID;		/**		* 单位地编别名		*/		public string Name;		/**		* 地图ID		*/		public int MapID;		/**		* 名称等级图标		*/		public string LevelIcon;		/**		* 备注		*/		public string Desc;		/**		* X偏移		*/		public int OffsetX;		/**		* Y偏移		*/		public int OffsetY;		/**		* 缩放		*/		public int Scale;		static public Dictionary<int, TbDataWorldBoss> temples=new Dictionary<int,TbDataWorldBoss>();		static public void initdata(Dictionary<int,Hashtable> table){			foreach(Hashtable tb in table.Values){			try{				TbDataWorldBoss tp=new TbDataWorldBoss();				temples[(int)tb["Id"]] = tp;				tp.Id=(int)tb["Id"];				tp.BossBaseID=(int)tb["BossBaseID"];				tp.Name=(string)tb["Name"];				tp.MapID=(int)tb["MapID"];				tp.LevelIcon=(string)tb["LevelIcon"];				tp.Desc=(string)tb["Desc"];				tp.OffsetX=(int)tb["OffsetX"];				tp.OffsetY=(int)tb["OffsetY"];				tp.Scale=(int)tb["Scale"];			}catch(System.Exception ee){				System.Console.WriteLine(ee);			}			}		}	static public TbDataWorldBoss select(int id) {		if (temples.ContainsKey(id)) {			return temples[id];		}		return null;	}	}}